import React, { useState } from "react";


import Button from "@mui/material/Button";
import DeleteIcon from "@mui/icons-material/Delete";

const ActionButton = ({
  position,
  userList,
  setUserList,
  action,
  handleShow,
  userValues,
  buttonType,
  currentUser,
}) => {
  const [color, setColor] = useState("error")
  const handleUser = () => {
    
    if (action === "delete") {
      setColor("error")
      setUserList(
        userList.filter((user) => position !== userList.indexOf(user))
      );
    } else if (buttonType === "new") {
      setColor("primary")
      setUserList([...userList, userValues]);
      handleShow();
      console.log(color)
    } else if (buttonType === "edit") {
      setColor("warning")
      const updateUser = [...userList];
      updateUser[currentUser] = userValues;

      setUserList([...updateUser]);
      handleShow();
    }
  };
  const icon = <DeleteIcon></DeleteIcon>


  return (
    // <Stack direction="row">
    <Button variant="contained" color={color} onClick={handleUser}>
      {icon}
    </Button>
    // </Stack>
  );
};

export default ActionButton;
