import React from "react";
import { useState, useContext } from "react";

import { UserContext } from "./App";
import ActionButton from "./ActionButton";
import Modal from "@mui/material/Modal";
import CloseIcon from "@mui/icons-material/Close";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import Button from "@mui/material/Button";

import "../modal.css";

const MyModal = () => {
  const { handleShow, shown } = useContext(UserContext);
  const [modalShown, setModalShown] = shown;
  const [userValues, setUserValues] = useState({
    id: 0,
    firstName: "",
    lastName: "",
    email: "",
    age: 0,
  });

  const handleUserValueChange = (e) => {
    const { name, value } = e.target;
    setUserValues({
      ...userValues,
      [name]: value,
    });
  };

  return (
    // <div className="form-wrapper">
    //   <div className="wrapper2">
    //     <div className="form-header">
    //       <span>
    //         <h2>User details</h2>
    //       </span>
    //       <Button onClick={handleShow} color="error" size="medium">
    //         <CloseIcon></CloseIcon>
    //       </Button>
    //     </div>
    //     <div>
    <Modal
      open={modalShown}
      onClose={handleShow}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <form className="user-form">
        <Grid
          container
          alignItems="center"
          justifyContent="center"
          direction="column"
        >
          <Grid item></Grid>
          <Grid item>
            <TextField
              id="first-input"
              name="firstName"
              label="First Name"
              type="text"
              value={userValues.name}
              onChange={handleUserValueChange}
            />
          </Grid>
          <Grid item>
            <TextField
              id="last-name"
              name="lastName"
              label="Last Name"
              type="text"
              value={userValues.name}
              onChange={handleUserValueChange}
            />
          </Grid>
          <Grid item>
            <TextField
              id="email"
              name="email"
              label="Email"
              type="text"
              value={userValues.name}
              onChange={handleUserValueChange}
            />
          </Grid>
          <Grid item>
            <TextField
              id="age"
              name="age"
              label="Age"
              type="number"
              value={userValues.name}
              onChange={handleUserValueChange}
            />
          </Grid>
          <ActionButton userValues={userValues} action={"edit"} />
        </Grid>
      </form>
    </Modal>
    //   </div>
    // </div>
  );
};

export default MyModal;
