import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useChangeButtonMutation, useGetButtonQuery } from "../redux/slices/apiSlice";
import { changeModal } from "../redux/slices/buttonSlice";
import { v4 as uuidv4 } from "uuid";

import ActionButton from "./ActionButton";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import CloseIcon from "@mui/icons-material/Close";
import Grid from "@material-ui/core/Grid";
import Modal from "@mui/material/Modal";
import TextField from "@material-ui/core/TextField";
import Typography from "@mui/material/Typography";

import "../modal.css";

const MyModal = () => {

  //API OPERATIONS
  const [changeButton] = useChangeButtonMutation()
  const { data, isFetching } = useGetButtonQuery();
  //API OPERATIONS



  const type = useSelector((state) => state.button.show);
  const dispatch = useDispatch();

  const [userValues, setUserValues] = useState({
    id: uuidv4(),
    firstName: "",
    lastName: "",
    email: "",
    age: 0,
  });

  const handleUserValueChange = (e) => {
    const { name, value } = e.target;
    setUserValues({
      ...userValues,
      [name]: value,
    });
  };
  const onCloseHandle = () => {
    dispatch(changeModal(!type))
    changeButton({show: false, type: ""})

  }
  if (isFetching) return "<Backdrop open={true}><CircularProgress /></Backdrop>;"

  return (
    <Modal
      className="modal"
      //open={type}
      open={data.show}
      onClose={onCloseHandle}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <Box className="modal-box">
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          {" "}
          <Typography variant="h5">User details</Typography>
          <Button
            onClick={() => dispatch(changeModal(!type))}
            color="error"
            size="medium"
          >
            <CloseIcon />
          </Button>
        </div>

        <form className="user-form">
          <Grid
            container
            alignItems="center"
            justifyContent="center"
            direction="column"
          >
            <Grid item>
              <TextField
                id="first-input"
                name="firstName"
                label="First Name"
                type="text"
                value={userValues.name}
                onChange={handleUserValueChange}
              />
            </Grid>
            <Grid item>
              <TextField
                id="last-name"
                name="lastName"
                label="Last Name"
                type="text"
                value={userValues.name}
                onChange={handleUserValueChange}
              />
            </Grid>
            <Grid item>
              <TextField
                id="email"
                name="email"
                label="Email"
                type="text"
                value={userValues.name}
                onChange={handleUserValueChange}
              />
            </Grid>
            <Grid item>
              <TextField
                id="age"
                name="age"
                label="Age"
                type="number"
                value={userValues.name}
                onChange={handleUserValueChange}
              />
            </Grid>
            <ActionButton userValues={userValues} action={"edit"} />
          </Grid>
        </form>
      </Box>
    </Modal>
  );
};

export default MyModal;
