import React from "react";

// import { useDispatch, useSelector } from "react-redux";
// import { changeButtonType, changeModal } from "../redux/slices/buttonSlice";
// import { changeCurrentUser } from "../redux/slices/currentUserSlice";
import {
  useChangeButtonMutation,
  useChangeCurrentUserMutation,
  useGetButtonQuery,
} from "../redux/slices/apiSlice";

import Button from "@mui/material/Button";
import EditIcon from "@mui/icons-material/Edit";

const ModifyButton = ({ action, position }) => {
  const userPosition = position
  
  //API OPERATIONS
  const [changeButton] = useChangeButtonMutation();
  const { data } = useGetButtonQuery();
  const [changeCurrentUser] = useChangeCurrentUserMutation();

  //API OPERATIONS
  // const dispatch = useDispatch();
  // const type = useSelector((state) => state.button.show);
  const handleButtonClick = (userPosition) => {
    changeButton({ show: !data.show, type: action });
    // dispatch(changeModal(!type));
    // dispatch(changeButtonType(action));

    if (userPosition >= 0) {
      changeCurrentUser({position});
      
      // dispatch(changeCurrentUser(position));
    }
  };

  if (action === "new") {
    return (
      <Button variant="contained" onClick={handleButtonClick}>
        New
      </Button>
    );
  }
  if (action === "edit") {
    return (
      <Button variant="contained" color="warning" onClick={handleButtonClick}>
        <EditIcon />
      </Button>
    );
  }
};

export default ModifyButton;
