import React from "react";

import Header from "./Header";
import ListBody from "./ListBody";
import MyModal from "./Modal";

// import { useSelector } from "react-redux";
import { useGetButtonQuery } from "../redux/slices/apiSlice";

import { CircularProgress } from "@mui/material";
import { Backdrop } from "@mui/material";



const App = () => {
  // const type = useSelector((state) => state.button.show);

//API OPERATIONS
const { data, isFetching } = useGetButtonQuery();
//API OPERATIONS
  
  //  console.log(data)
  
  if (isFetching) return <Backdrop open={true}><CircularProgress /></Backdrop>;

  return (
    <>
      <Header />
      <ListBody />
      {data?.show && <MyModal />}
    </>
  );
};

export default App;
