import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";

export const usersApi = createApi({
  reducerPath: "usersApi",
  baseQuery: fetchBaseQuery({ baseUrl: "http://localhost:5001" }),
  tagTypes: ["Users", "Button", "CurrentUser"],
  endpoints: (builder) => ({
    getUsers: builder.query({
      query: () => "/",
      providesTags: ["Users"],
    }),
    addUser: builder.mutation({
      query: (user) => ({
        url: "/",
        method: "POST",
        body: user,
      }),
      invalidatesTags: ["Users"],
    }),
    updateUser: builder.mutation({
      query: (user) => ({
        url: "/",
        method: "PUT",
        body: user,
      }),
      invalidatesTags: ["Users"],
    }),
    removeUser: builder.mutation({
      query: (id) => ({
        url: "/",
        method: "DELETE",
        body: id,
      }),
      invalidatesTags: ["Users"],
    }),
    getButton: builder.query({
      query: () => "/button",
      providesTags: ["Button"],
    }),
    changeButton: builder.mutation({
      query: (button) => ({
        url: "/button",
        method: "PUT",
        body: button,
      }),
      invalidatesTags: ["Button"],
    }),
    getCurrentUser: builder.query({
      query: () => "/currentuser",
      providesTags: ["CurrentUser"],
    }),
    changeCurrentUser: builder.mutation({
      query: (position) => ({
        url: "/currentuser",
        method: "PUT",
        body: position,
      }),
      invalidatesTags: ["CurrentUser"],
    }),

    getButton: builder.query({
      query: () => "/button",
    }),
    getCurrentUser: builder.query({
      query: () => "/currentuser",
    }),
  }),
});
export const {
  useGetUsersQuery,
  useAddUserMutation,
  useUpdateUserMutation,
  useRemoveUserMutation,
  useGetButtonQuery,
  useChangeButtonMutation,
  useGetCurrentUserQuery,
  useChangeCurrentUserMutation,
} = usersApi;
export default usersApi.reducer;
