import { createSlice } from "@reduxjs/toolkit";
import { v4 as uuidv4 } from "uuid";
import produce from "immer";

const userSlice = createSlice({
  name: "users",

  initialState: [
    {
      id: uuidv4(),
      firstName: "John",
      lastName: "Doe",
      email: "johndoe@gmail.com",
      age: 27,
    },
    {
      id: uuidv4(),
      firstName: "Lech",
      lastName: "Nowak",
      email: "nowaklech@gmail.com",
      age: 25,
    },
    {
      id: uuidv4(),
      firstName: "Jan",
      lastName: "Kowalski",
      email: "jankowalski@gmail.com",
      age: 43,
    },
  ],
  reducers: {
    addUser: (state, action) => {
      return produce(state, (draftState) => {
        draftState.push(action.payload);
      });
    },
    deleteUser: (state, action) => {
        return produce(state, (draftState) => {
            draftState = state.filter((user) => action.payload !== state.indexOf(user));
          });
        
    //   return state.filter((user) => action.payload !== state.indexOf(user));
    },
    editUser: (state, action) => {
      const index = state.findIndex((entry) => entry.id === action.payload.id);
      console.log(index);
      console.log(action.payload.id);
      return produce(state, (draftState) => {
        draftState[index] = action.payload;
      });
      //   return produce(state, (draftState) => {
      //     draftState[action.payload.currentUser] = action.payload
      //   });
    },
  },
});

export const { addUser, deleteUser, editUser } = userSlice.actions;
export default userSlice.reducer;
